import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { catchError, map, of, switchMap, tap } from 'rxjs';
import { Customer } from 'src/app/shared/Customer.Model';
import { AuthService } from '../auth.service';
import * as AuthActions from './auth.actions';

export interface AuthResponseData {
  firstName: string;
  lastName: string;
  email: string;
  token: string;
  expiresIn: number;
}

const handleAuthentication = (
  firstName: string,
  lastName: string,
  email: string,
  token: string,
  expiresIn: number
) => {
  const expirationDate = new Date(new Date().getTime() + expiresIn * 1000);
  const user = new Customer(firstName, lastName, email, token, expirationDate);
  localStorage.setItem('customer', JSON.stringify(user));
  return AuthActions.authenticateSuccess({
    firstName,
    lastName,
    email,
    token,
    expirationDate,
    redirect: true,
  });
};

const handleError = (errorRes: any) => {
  console.log(errorRes);
  let errorMessage = 'An unknown error occurred!';
  if (!errorRes.error || errorRes.statusText === 'Unknown Error') {
    return of(AuthActions.authenticateFail({ errorMessage }));
  }
  if (errorRes.error.Message) {
    errorMessage = errorRes.error.Message;
  } else {
    errorMessage = 'Wrong email or password';
  }

  return of(AuthActions.authenticateFail({ errorMessage }));
};
const handleRegisterError = (errorRes: any) => {
  console.log(errorRes);

  let errorMessage = 'An unknown error occurred!';
  if (!errorRes.error) {
    return of(AuthActions.authenticateFail({ errorMessage }));
  }
  var errors = Object.values(errorRes.error);
  errorMessage = errors[0][0];
  return of(AuthActions.authenticateFail({ errorMessage }));
};

@Injectable()
export class AuthEffects {
  authLogin$ = createEffect(() =>
    this.actions$.pipe(
      ofType(AuthActions.loginStart),
      switchMap((action) => {
        return this.http
          .post('https://localhost:7112/api/authenticate/login', {
            Email: action.email,
            Password: action.password,
          })
          .pipe(
            map((response: AuthResponseData) => {
              console.log(response);

              return handleAuthentication(
                response.firstName,
                response.lastName,
                response.email,
                response.token,
                response.expiresIn
              );
            }),
            catchError((errorRes) => {
              return handleError(errorRes);
            })
          );
      })
    )
  );
  authRegister$ = createEffect(() =>
    this.actions$.pipe(
      ofType(AuthActions.signupStart),
      switchMap((action) => {
        return this.http
          .post('https://localhost:7112/api/authenticate/register', {
            FirstName: action.firstName,
            LastName: action.lastName,
            Email: action.email,
            Password: action.password,
          })
          .pipe(
            map((response: AuthResponseData) => {
              console.log(response);
              return handleAuthentication(
                response.firstName,
                response.lastName,
                response.email,
                response.token,
                response.expiresIn
              );
            }),
            catchError((errorRes) => {
              console.log(errorRes);

              return handleRegisterError(errorRes);
            })
          );
      })
    )
  );
  authRedirect$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(AuthActions.authenticateSuccess),
        tap((action) => action.redirect && this.router.navigate(['/']))
      ),
    { dispatch: false }
  );
  authLogout$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(AuthActions.logout),
        tap(() => {
          this.authService.clearLogoutTimer();
          localStorage.removeItem('customer');
          this.router.navigate(['/login']);
        })
      ),
    { dispatch: false }
  );
  // authAutoLogin$ = createEffect(
  //   () =>
  //     this.actions$.pipe(
  //       ofType(AuthActions.autoLogin),
  //       map(() => {
  //         const loadedCustomer:{

  //         } = JSON.parse(
  //           localStorage.getItem('customer')
  //         );
  //         if(!loadedCustomer)
  //         {
  //           return { type: 'DUMMY' };
  //         }
  //         if (loadedCustomer.token) {
  //           // this.user.next(loadedUser);
  //           const expirationDuration =
  //             new Date(loadedCustomer._tokenExpirationDate).getTime() -
  //             new Date().getTime();
  //           this.authService.setLogoutTimer(expirationDuration);
  //           // return AuthActions.authenticateSuccess({
  //           //   email: loadedCustomer.email,
  //           //   userId: loadedCustomer.customerId,
  //           //   token: loadedCustomer.token,
  //           //   expirationDate: new Date(loadedCustomer._tokenExpirationDate),
  //           //   redirect: false
  //           // });
  //       })
  //     ),
  //   { dispatch: false }
  // );
  constructor(
    private actions$: Actions,
    private http: HttpClient,
    private router: Router,
    private authService: AuthService
  ) {}
}
