import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { catchError, map, of, switchMap, tap } from 'rxjs';
import { Customer } from 'src/app/shared/Customer.Model';
import * as AuthActions from './auth.actions';

export interface AuthResponseData {
  firstName: string;
  lastName: string;
  email: string;
  token: string;
  expiresIn: number;
}

const handleAuthentication = (
  firstName: string,
  lastName: string,
  email: string,
  token: string,
  expiresIn: number
) => {
  const expirationDate = new Date(new Date().getTime() + expiresIn * 1000);
  const user = new Customer(firstName, lastName, email, token, expirationDate);
  localStorage.setItem('userData', JSON.stringify(user));
  return AuthActions.authenticateSuccess({
    firstName,
    lastName,
    email,
    token,
    expirationDate,
    redirect: true,
  });
};

const handleError = (errorRes: any) => {
  let errorMessage = 'An unknown error occurred!';
  if (!errorRes.error) {
    return of(AuthActions.authenticateFail({ errorMessage }));
  }
  if (errorRes.error.Message) {
    errorMessage = errorRes.error.Message;
  } else {
    errorMessage = 'Wrong email or password';
  }

  return of(AuthActions.authenticateFail({ errorMessage }));
};

@Injectable()
export class AuthEffects {
  authLogin$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(AuthActions.loginStart),
        switchMap((action) => {
          return this.http
            .post('https://localhost:7112/api/authenticate/login', {
              Email: action.email,
              Password: action.password,
            })
            .pipe(
              map((response: AuthResponseData) => {
                return handleAuthentication(
                  response.firstName,
                  response.lastName,
                  response.email,
                  response.token,
                  response.expiresIn
                );
              }),
              catchError((errorRes) => {
                return handleError(errorRes);
              })
            );
        })
      ),
    { dispatch: false }
  );
  authRegister$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(AuthActions.signupStart),
        switchMap((action) => {
          return this.http
            .post('https://localhost:7112/api/authenticate/register', {
              Email: action.email,
              Password: action.password,
            })
            .pipe(
              map((response: AuthResponseData) => {
                return handleAuthentication(
                  response.firstName,
                  response.lastName,
                  response.email,
                  response.token,
                  response.expiresIn
                );
              }),
              catchError((errorRes) => {
                return handleError(errorRes);
              })
            );
        })
      ),
    { dispatch: false }
  );
  constructor(private actions$: Actions, private http: HttpClient) {}
}
