import { Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Store } from '@ngrx/store';
import * as AuthActions from '../store/auth.actions';
import * as fromApp from '../../store/app.reducer';
import { Subscription } from 'rxjs';
import { AlertComponent } from 'src/app/shared/alert/alert.component';
import { PlaceholderDirective } from 'src/app/shared/placeholder.directive';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent implements OnInit, OnDestroy {
  registerForm: FormGroup;
  storeSub: Subscription;
  @ViewChild(PlaceholderDirective, { static: false })
  placeholder: PlaceholderDirective;
  isLoading = false;
  errorMessage: string = null;

  constructor(private store: Store<fromApp.AppState>) {}

  initForm() {
    this.registerForm = new FormGroup({
      firstName: new FormControl('', Validators.required),
      lastName: new FormControl('', Validators.required),
      email: new FormControl('', [
        Validators.required,
        Validators.email,
        Validators.pattern(/(?=.*[0-9])/),
      ]),
      password: new FormControl('', [
        Validators.required,
        Validators.minLength(6),
      ]),
    });
  }
  ngOnInit(): void {
    this.initForm();
    this.storeSub = this.store.select('auth').subscribe({
      next: (authState) => {
        console.log(authState);
        this.errorMessage = authState.errorMessage;
        this.isLoading = authState.loading;
        if (this.errorMessage) {
          this.showErrorAlert(this.errorMessage);
        }
      },
    });
  }
  onSubmit() {
    console.log(this.registerForm.value);
    if (!this.registerForm.valid) {
      return;
    }
    const firstName = this.registerForm.value.firstName;
    const lastName = this.registerForm.value.lastName;
    const email = this.registerForm.value.email;
    const password = this.registerForm.value.password;

    this.store.dispatch(
      AuthActions.signupStart({
        firstName: firstName,
        lastName: lastName,
        email: email,
        password: password,
      })
    );
  }

  onClear() {
    this.registerForm.reset();
  }
  private showErrorAlert(error: string) {
    const containerRef = this.placeholder.viewContainerRef;
    containerRef.clear();
    const component = containerRef.createComponent(AlertComponent);
    component.instance.message = error;
    const event = component.instance.closed.subscribe(() => {
      event.unsubscribe();
      containerRef.clear();
    });
  }

  ngOnDestroy(): void {
    this.storeSub.unsubscribe();
  }
}
<ng-template appPlaceholder></ng-template>
<div class="row">
  <app-loading-spinner *ngIf="isLoading"></app-loading-spinner>
  <div *ngIf="!isLoading">
    <div class="col-sm-12 col-xs-12 col-md-4 col-lg-4">
      <h1>Register:</h1>
    </div>
    <div class="col-sm-12 col-xs-12 col-md-6 col-lg-6">
      <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
        <div class="m-2">
          <label class="form-label" for="firstName">First Name:</label>
          <input
            class="form-control"
            type="text"
            id="firstName"
            formControlName="firstName"
          />
        </div>
        <div class="m-2">
          <label class="form-label" for="lastName">Last Name:</label>
          <input
            class="form-control"
            type="text"
            id="lastName"
            formControlName="lastName"
          />
        </div>
        <div class="m-2">
          <label class="form-label" for="email">Email Address:</label>
          <input
            class="form-control"
            type="email"
            id="email"
            formControlName="email"
          />
        </div>
        <div class="m-2">
          <label class="form-label" for="password">Password:</label>
          <input
            class="form-control"
            type="password"
            id="password"
            formControlName="password"
            [placeholder]="'Password should contain atleast 1 digit'"
          />
        </div>

        <div class="m-2 my-3">
          <button
            class="btn btn-outline-primary"
            type="submit"
            [disabled]="!registerForm.valid"
          >
            Register
          </button>
          <button
            class="btn btn-outline-warning mx-3"
            type="button"
            (click)="onClear()"
          >
            Clear
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
